		"shaders\\default\\shadow_blur.frag": "/**\r\n * Shadow blur\r\n */\r\n\r\nprecision highp float;\r\n\r\nuniform sampler2D src;\r\n\r\nvarying vec2 uv;\r\nvarying highp vec2 blurCoords[14];\r\n\r\nvoid main () {\r\n\tlowp vec4 sum = vec4(0.0);\r\n\tsum += texture2D(src, blurCoords[ 0])*0.0044299121055113265;\r\n\tsum += texture2D(src, blurCoords[ 1])*0.00895781211794;\r\n\tsum += texture2D(src, blurCoords[ 2])*0.0215963866053;\r\n\tsum += texture2D(src, blurCoords[ 3])*0.0443683338718;\r\n\tsum += texture2D(src, blurCoords[ 4])*0.0776744219933;\r\n\tsum += texture2D(src, blurCoords[ 5])*0.115876621105;\r\n\tsum += texture2D(src, blurCoords[ 6])*0.147308056121;\r\n\tsum += texture2D(src, uv            )*0.159576912161;\r\n\tsum += texture2D(src, blurCoords[ 7])*0.147308056121;\r\n\tsum += texture2D(src, blurCoords[ 8])*0.115876621105;\r\n\tsum += texture2D(src, blurCoords[ 9])*0.0776744219933;\r\n\tsum += texture2D(src, blurCoords[10])*0.0443683338718;\r\n\tsum += texture2D(src, blurCoords[11])*0.0215963866053;\r\n\tsum += texture2D(src, blurCoords[12])*0.00895781211794;\r\n\tsum += texture2D(src, blurCoords[13])*0.0044299121055113265;\r\n\tgl_FragColor = sum;\r\n}\r\n",
		"shaders\\default\\shadow_blurh.vert": "/**\r\n * Shadow blur - horizontal\r\n */\r\n\r\nattribute vec3 position;\r\nattribute vec2 uv0;\r\n\r\nuniform sampler2D src;\r\n\r\nvarying vec2 uv;\r\nvarying vec2 blurCoords[14];\r\n\r\nvoid main() {\r\n\tfloat blurSize = 0.2;\r\n\tblurCoords[ 0] = uv0 + vec2(-0.028, 0.0) * blurSize;\r\n\tblurCoords[ 1] = uv0 + vec2(-0.024, 0.0) * blurSize;\r\n\tblurCoords[ 2] = uv0 + vec2(-0.020, 0.0) * blurSize;\r\n\tblurCoords[ 3] = uv0 + vec2(-0.016, 0.0) * blurSize;\r\n\tblurCoords[ 4] = uv0 + vec2(-0.012, 0.0) * blurSize;\r\n\tblurCoords[ 5] = uv0 + vec2(-0.008, 0.0) * blurSize;\r\n\tblurCoords[ 6] = uv0 + vec2(-0.004, 0.0) * blurSize;\r\n\tblurCoords[ 7] = uv0 + vec2( 0.004, 0.0) * blurSize;\r\n\tblurCoords[ 8] = uv0 + vec2( 0.008, 0.0) * blurSize;\r\n\tblurCoords[ 9] = uv0 + vec2( 0.012, 0.0) * blurSize;\r\n\tblurCoords[10] = uv0 + vec2( 0.016, 0.0) * blurSize;\r\n\tblurCoords[11] = uv0 + vec2( 0.020, 0.0) * blurSize;\r\n\tblurCoords[12] = uv0 + vec2( 0.024, 0.0) * blurSize;\r\n\tblurCoords[13] = uv0 + vec2( 0.028, 0.0) * blurSize;\r\n\tuv = uv0;\r\n\tgl_Position = vec4(position.xy, 0.0, 1.0);\r\n}\r\n",
